%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
% Figures in the
%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
\hypersetup{breaklinks=true}
\newlength{\Textw} % save textwidth outside the boxes
\setlength{\Textw}{\textwidth}
\newlength{\Hshift}
\newlength{\Mshift}
\newcommand*{\calculateMshift}{%
  \checkoddpage
  \ifoddpage
    \setlength{\Mshift}{\marginparsep}
  \else
    \setlength{\Mshift}{\dimexpr-\marginparsep-\textwidth-\marginparwidth\relax}
  \fi
}

\newcommand*{\calculateHshift}{%
  \checkoddpage
  \ifoddpage
    \setlength{\Hshift}{\dimexpr\Textw/2-\tcbtextwidth/2\relax}
  \else
    \setlength{\Hshift}{\dimexpr-\Textw/2+\tcbtextwidth/2\relax}
  \fi
}
\newcommand{\tcbmarginbox}[2]{%
  \par %start a new line
  \calculateMshift
  \calculateHshift
  \SetHorizontalCoffin\Framex{} %clear box Framex
  \SetVerticalCoffin\Theox{\marginparwidth}{#1}% fill box \Theox
  \JoinCoffins*\Framex[r,vc]\Theox[l,vc](\dimexpr\Mshift+\textwidth\relax,#2)%join boxes
  \noindent\TypesetCoffin\Framex(\Hshift,0pt)\\[-2\baselineskip] %typset assembly
}
\newcommand{\listmarginbox}[2]{%
  \par %start a new line
  \calculateMshift
  \calculateHshift
  \SetHorizontalCoffin\Framex{\color{black}\rule{\tcbtextwidth}{0pt}} %clear box Framex
  \SetVerticalCoffin\Theox{\marginparwidth}{#1}% fill box \Theox
  \JoinCoffins*\Framex[r,vc]\Theox[l,vc](\dimexpr\Mshift+\Hshift-9mm\relax,#2)%join boxes
  \noindent\TypesetCoffin\Framex\\[-2\baselineskip] %typeset assembly
}
\newcommand{\parmarginbox}[2]{%
  \par %start a new line
  \calculateMshift
  \SetHorizontalCoffin\Framex{}
  \SetVerticalCoffin\Theox{\marginparwidth}{#1}
  \JoinCoffins*\Framex[r,vc]\Theox[l,vc](\dimexpr\Mshift+\textwidth\relax,#2)
  \noindent\TypesetCoffin\Framex(0mm,0pt)\\[-2\baselineskip]
}
